import React, { useState, useEffect } from "react";
import axios from "axios";

const Details = () => {
  const [cryptoData, setCryptoData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3000/crypto-data");
        setCryptoData(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!cryptoData || !cryptoData.data || !cryptoData.data.item) {
    return <div>Data not available</div>;
  }

  const { latestRate, specificData } = cryptoData.data.item;

  const formatChange = (change) => {
    const roundedChange = parseFloat(change).toFixed(2);
    return roundedChange;
  };

  return (
    <div>
      <h1>Bitcoin Market Data</h1>
      <table className="crypto-details-table">
        <tbody>
          <tr>
            <td rowSpan={2} className="crypto-image">
              <img
                src={`data:image/png;base64,${cryptoData.data.item.assetLogo.imageData}`}
                alt="Bitcoin"
              />
            </td>
            <td colSpan={3}><h2>Bitcoin</h2></td>
          </tr>
          <tr>
            <td><strong>Value:</strong> ${latestRate.amount}</td>
            <td><strong>Market Cap:</strong> {specificData.marketCapInUSD}</td>
            <td><strong>Asset Type:</strong> {specificData.assetType}</td>
          </tr>
          <tr>
            <td><strong>Circulating Supply:</strong> {specificData.circulatingSupply}</td>
            <td><strong>Maximum Supply:</strong> {specificData.maxSupply}</td>
            <td colSpan={2}>
              <div className="change-info">
                <p><strong>Change (1 Hour):</strong> {formatChange(specificData["1HourPriceChangeInPercentage"])}%</p>
                <p><strong>Change (1 Day):</strong> {formatChange(specificData["24HoursPriceChangeInPercentage"])}%</p>
                <p><strong>Change (1 Week):</strong> {formatChange(specificData["1WeekPriceChangeInPercentage"])}%</p>
              </div>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default Details;
