import React, { useState, useEffect } from "react";
import axios from "axios";

const Details = () => {
  const [cryptoData, setCryptoData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3000/crypto-data");
        setCryptoData(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!cryptoData || !cryptoData.data || !cryptoData.data.item) {
    return <div>Data not available</div>;
  }

  const { latestRate, specificData } = cryptoData.data.item;

  return (
    <div>
      <h1>Bitcoin Market Data</h1>
      <div style={{ display: "flex", alignItems: "center" }}>
        <div>
          <img
            src={`data:image/png;base64,${cryptoData.data.item.assetLogo.imageData}`}
            alt="Bitcoin"
            style={{ width: "100px", height: "100px", marginRight: "20px" }}
          />
        </div>
        <div>
          <h2>Bitcoin</h2>
          <p>Value: ${latestRate.amount}</p>
          <p>Market Cap: {specificData.marketCapInUSD}</p>
          <p>Asset Type: {specificData.assetType}</p>
          <p>Circulating Supply: {specificData.circulatingSupply}</p>
          <p>Maximum Supply: {specificData.maxSupply}</p>
          <p>Change (1 Hour): {specificData["1HourPriceChangeInPercentage"]}%</p>
          <p>Change (1 Day): {specificData["24HoursPriceChangeInPercentage"]}%</p>
          <p>Change (1 Week): {specificData["1WeekPriceChangeInPercentage"]}%</p>
        </div>
      </div>
    </div>
  );
};

export default Details;
